import React, { useEffect, useState, useCallback } from 'react';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend, Filler } from 'chart.js';
import { Bar, Doughnut, Line } from 'react-chartjs-2';
import adminService from '../services/adminService';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, ArcElement, Title, Tooltip, Legend, Filler);

const Dashboard = () => {
    const [stats, setStats] = useState(null);
    const [categoryStats, setCategoryStats] = useState(null);
    const [recoveryRate, setRecoveryRate] = useState(null);
    const [loading, setLoading] = useState(true);
    const [refreshing, setRefreshing] = useState(false);

    const fetchStats = useCallback(async (isSilent = false) => {
        if (!isSilent) setLoading(true);
        else setRefreshing(true);

        try {
            // Fetch main stats
            const statsRes = await adminService.getDashboardStats();
            setStats(statsRes.data);

            // Fetch category stats
            try {
                const categoryRes = await adminService.getCategoryStats();
                setCategoryStats(categoryRes.data);
            } catch (e) {
                console.error('Category stats fetch failed:', e);
            }

            // Fetch recovery rate
            try {
                const recoveryRes = await adminService.getRecoveryRate();
                setRecoveryRate(recoveryRes.data);
                console.log('Recovery Rate Data:', recoveryRes.data);
            } catch (e) {
                console.error('Recovery rate fetch failed:', e);
            }

        } catch (error) {
            console.error('Core Dashboard stats fetch failed:', error);
        } finally {
            setLoading(false);
            setRefreshing(false);
        }
    }, []);

    useEffect(() => {
        fetchStats();
        const interval = setInterval(() => fetchStats(true), 60000);
        return () => clearInterval(interval);
    }, [fetchStats]);

    if (loading) return (
        <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '80vh', flexDirection: 'column', color: '#6c5ce7' }}>
            <div className="spinner" style={{ width: '50px', height: '50px', border: '5px solid #f3f3f3', borderTop: '5px solid #6c5ce7', borderRadius: '50%', animation: 'spin 1s linear infinite' }}></div>
            <p style={{ marginTop: '20px', fontWeight: '600' }}>Synchronizing Campus Inventory...</p>
            <style>{`@keyframes spin { 0% { transform: rotate(0deg); } 100% { transform: rotate(360deg); } }`}</style>
        </div>
    );

    if (!stats) return (
        <div style={{ textAlign: 'center', padding: '100px' }}>
            <h2 style={{ color: '#d63031' }}>Access Denied or Connection Lost</h2>
            <p>Could not retrieve intelligence from the backend. Please verify your authentication and server status.</p>
            <button onClick={() => fetchStats()} className="btn" style={{ backgroundColor: '#6c5ce7', color: 'white', marginTop: '20px' }}>Retry Connection</button>
        </div>
    );

    const chartColors = [
        'rgba(108, 92, 231, 0.8)',
        'rgba(253, 121, 168, 0.8)',
        'rgba(0, 184, 148, 0.8)',
        'rgba(255, 118, 117, 0.8)',
        'rgba(250, 177, 160, 0.8)',
        'rgba(116, 185, 255, 0.8)',
        'rgba(162, 155, 254, 0.8)',
        'rgba(255, 234, 167, 0.8)'
    ];

    return (
        <div style={{ padding: '30px', backgroundColor: '#f4f7f6', minHeight: '100vh' }}>
            <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
                <div>
                    <h1 style={{ margin: 0, color: '#2d3436', fontSize: '2.5rem', fontWeight: '800' }}>Command Center</h1>
                    <p style={{ color: '#636e72', margin: '5px 0 0 0' }}>Real-time oversight of Rajalakshmi Engineering College's Lost & Found ecosystem.</p>
                </div>
                <div style={{ textAlign: 'right' }}>
                    <button
                        onClick={() => fetchStats(true)}
                        disabled={refreshing}
                        style={{
                            padding: '10px 20px',
                            backgroundColor: 'white',
                            border: '1px solid #dfe6e9',
                            borderRadius: '10px',
                            cursor: 'pointer',
                            color: '#6c5ce7',
                            fontWeight: '600',
                            transition: 'all 0.3s'
                        }}
                    >
                        {refreshing ? 'Syncing...' : 'Refresh Intelligence'}
                    </button>
                    <div style={{ fontSize: '11px', color: '#b2bec3', marginTop: '5px' }}>Auto-sync every 60s</div>
                </div>
            </div>

            {/* Premium Metrics Grid */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(240px, 1fr))', gap: '20px', marginBottom: '30px' }}>
                {[
                    { label: 'Lost Reports', value: stats.total_lost, color: '#d63031', icon: 'ðŸ”' },
                    { label: 'Found Items', value: stats.total_found, color: '#00b894', icon: 'ðŸ“¦' },
                    { label: 'Verification Queue', value: stats.pending_verification, color: '#fdcb6e', icon: 'â³' },
                    { label: 'Ready for Collection', value: stats.available_items, color: '#0984e3', icon: 'âœ…' },
                    { label: 'Pending Claims', value: stats.pending_claims, color: '#6c5ce7', icon: 'ðŸ“‘' },
                    { label: 'Resolved Today', value: stats.returned_today, color: '#e17055', icon: 'ðŸŽ‰' }
                ].map((metric, i) => (
                    <div key={i} style={{
                        backgroundColor: 'white',
                        padding: '24px',
                        borderRadius: '20px',
                        boxShadow: '0 10px 20px rgba(0,0,0,0.03)',
                        borderBottom: `4px solid ${metric.color}`
                    }}>
                        <div style={{ fontSize: '24px', marginBottom: '10px' }}>{metric.icon}</div>
                        <div style={{ fontSize: '32px', fontWeight: '800', color: '#2d3436' }}>{metric.value}</div>
                        <div style={{ fontSize: '14px', color: '#636e72', fontWeight: '600' }}>{metric.label}</div>
                    </div>
                ))}
            </div>

            {/* Advanced Analytics Section */}
            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(450px, 1fr))', gap: '25px', marginBottom: '30px' }}>

                {/* Items Category Intelligence */}
                <div style={{ backgroundColor: 'white', padding: '30px', borderRadius: '24px', boxShadow: '0 10px 30px rgba(0,0,0,0.04)' }}>
                    <h3 style={{ marginTop: 0, marginBottom: '25px', color: '#2d3436', display: 'flex', alignItems: 'center' }}>
                        <span style={{ marginRight: '10px' }}>ðŸ“Š</span> Category Distribution
                    </h3>
                    {categoryStats ? (
                        <div style={{ height: '300px' }}>
                            <Bar
                                data={{
                                    labels: Object.keys(categoryStats),
                                    datasets: [{
                                        label: 'Item Volume',
                                        data: Object.values(categoryStats),
                                        backgroundColor: chartColors,
                                        borderRadius: 8,
                                        borderSkipped: false,
                                    }]
                                }}
                                options={{
                                    responsive: true,
                                    maintainAspectRatio: false,
                                    plugins: { legend: { display: false } },
                                    scales: {
                                        y: { grid: { display: false }, border: { display: false } },
                                        x: { grid: { display: false }, border: { display: false } }
                                    }
                                }}
                            />
                        </div>
                    ) : (
                        <div style={{ height: '300px', display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundColor: '#f8f9fa', borderRadius: '15px', color: '#b2bec3' }}>
                            Awaiting categorical data...
                        </div>
                    )}
                </div>

                {/* Recovery Success Chart */}
                <div style={{ backgroundColor: 'white', padding: '30px', borderRadius: '24px', boxShadow: '0 10px 30px rgba(0,0,0,0.04)' }}>
                    <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '25px' }}>
                        <h3 style={{ margin: 0, color: '#2d3436', display: 'flex', alignItems: 'center' }}>
                            <span style={{ marginRight: '10px' }}>ðŸŽ¯</span> Recovery Mastery
                        </h3>
                        {recoveryRate && (
                            <div style={{
                                padding: '6px 12px',
                                backgroundColor: '#e1f5fe',
                                color: '#0288d1',
                                borderRadius: '30px',
                                fontSize: '12px',
                                fontWeight: '700'
                            }}>
                                TARGET: 85%
                            </div>
                        )}
                    </div>

                    {recoveryRate ? (
                        <div style={{ display: 'flex', alignItems: 'center', height: '300px' }}>
                            <div style={{ flex: 1, position: 'relative', height: '100%' }}>
                                <Doughnut
                                    data={{
                                        labels: ['Returned', 'In Repository'],
                                        datasets: [{
                                            data: [recoveryRate.returned, recoveryRate.total_found - recoveryRate.returned],
                                            backgroundColor: ['#00b894', '#6c5ce7'],
                                            borderWidth: 0,
                                            hoverOffset: 15,
                                            cutout: '70%'
                                        }]
                                    }}
                                    options={{
                                        responsive: true,
                                        maintainAspectRatio: false,
                                        plugins: { legend: { position: 'bottom' } }
                                    }}
                                />
                                <div style={{
                                    position: 'absolute', top: '45%', left: '50%', transform: 'translate(-50%, -50%)',
                                    textAlign: 'center'
                                }}>
                                    <div style={{ fontSize: '36px', fontWeight: '900', color: '#2d3436' }}>
                                        {recoveryRate.recovery_rate_percent}%
                                    </div>
                                    <div style={{ fontSize: '12px', color: '#636e72', fontWeight: '600' }}>SUCCESS RATE</div>
                                </div>
                            </div>
                            <div style={{ width: '180px', paddingLeft: '30px' }}>
                                <div style={{ marginBottom: '20px' }}>
                                    <div style={{ fontSize: '11px', color: '#b2bec3', fontWeight: '800' }}>OPERATIONAL YIELD</div>
                                    <div style={{ fontSize: '20px', fontWeight: '700', color: '#2d3436' }}>{recoveryRate.returned} Items</div>
                                    <div style={{ fontSize: '12px', color: '#636e72' }}>Safely returned to owners</div>
                                </div>
                                <div>
                                    <div style={{ fontSize: '11px', color: '#b2bec3', fontWeight: '800' }}>ACTIVE POOL</div>
                                    <div style={{ fontSize: '20px', fontWeight: '700', color: '#2d3436' }}>{recoveryRate.total_found} Items</div>
                                    <div style={{ fontSize: '12px', color: '#636e72' }}>Total discoveries recorded</div>
                                </div>
                            </div>
                        </div>
                    ) : (
                        <div style={{ height: '300px', display: 'flex', alignItems: 'center', justifyContent: 'center', backgroundColor: '#f8f9fa', borderRadius: '15px', color: '#b2bec3' }}>
                            Calculating recovery metrics...
                        </div>
                    )}
                </div>
            </div>

            {/* Grid for Bottom Actions & Status */}
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 350px', gap: '25px' }}>
                <div style={{ backgroundColor: 'white', padding: '30px', borderRadius: '24px', boxShadow: '0 10px 30px rgba(0,0,0,0.04)' }}>
                    <h3 style={{ marginTop: 0, marginBottom: '20px', color: '#2d3436' }}>System Health & Integrity</h3>
                    <div style={{ display: 'grid', gridTemplateColumns: 'repeat(3, 1fr)', gap: '15px' }}>
                        {[
                            { label: 'Database Status', value: 'OPTIMAL', color: '#00b894' },
                            { label: 'API Connection', value: 'STABLE', color: '#00b894' },
                            { label: 'Cloud Storage', value: 'SECURE', color: '#00b894' }
                        ].map((status, i) => (
                            <div key={i} style={{ backgroundColor: '#f8fafc', padding: '20px', borderRadius: '16px', textAlign: 'center' }}>
                                <div style={{ fontSize: '11px', color: '#64748b', fontWeight: '700', marginBottom: '5px' }}>{status.label}</div>
                                <div style={{ fontSize: '16px', fontWeight: '800', color: status.color }}>{status.value}</div>
                            </div>
                        ))}
                    </div>
                </div>

                <div style={{
                    background: 'linear-gradient(135deg, #6c5ce7 0%, #a212eb 100%)',
                    padding: '30px',
                    borderRadius: '24px',
                    color: 'white',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center'
                }}>
                    <h3 style={{ margin: '0 0 10px 0', fontSize: '1.5rem' }}>Security Protocol</h3>
                    <p style={{ margin: '0 0 20px 0', fontSize: '14px', opacity: 0.9 }}>Last audit performed 12 minutes ago. All transactions recorded in immutable ledger.</p>
                    <button style={{
                        padding: '12px',
                        backgroundColor: 'white',
                        color: '#6c5ce7',
                        border: 'none',
                        borderRadius: '12px',
                        fontWeight: '700',
                        cursor: 'pointer'
                    }}>
                        Run Security Audit
                    </button>
                </div>
            </div>
        </div>
    );
};

export default Dashboard;
